mapper.select:

	SELECT #{GROUP_IDX} AS GROUP_IDX, IFNULL(MAX(FILE_IDX), 0)+1 AS FILE_IDX
	FROM FILE_MASTER
	WHERE GROUP_IDX = #{GROUP_IDX}

